mapper.select:
Select productcode
From producttable
Where productsubject = #{productsubject} and productsize = #{productsize} and productcolor = #{productcolor}

Select count(qnano) 
From qnaboard
Where productcode = #{productcode}

Select * From (Select Q.* , rownum as R From (Select * From qnaboard Where productcode = #{productcode} order by qnano desc) Q
Where rownum <= #{totalnum}
Where rownum <= ((#{page}-1)*10)+10
) Where R >= ((#{page}-1)*10)+1

Select * From (Select Q.* , rownum as R From (Select * From qnaboard order by qnano desc) Q
Where rownum <= #{totalnum}
Where rownum <= ((#{page}-1)*10)+10
) Where R >= ((#{page}-1)*10)+1

Select qnacontents , productcode , qnaanswercontents
From qnaboard
Where qnano = #{qnano}

mapper.update:
Update qnaboard
Set qnaanswer = 1 , qnaanswercontents = #{qnaanswercontents}
Where qnano = #{qnano}

mapper.select:
Select * From (Select Q.* , rownum as R From (Select * From noticeboard order by noticeno desc) Q
Where rownum <= #{totalnum}
Where rownum <= ((#{page}-1)*10)+10
) Where R >= ((#{page}-1)*10)+1

Select noticeno , noticesubject , noticecontents , to_char(noticedate,'YYYY-MM-DD') AS noticedate 
From noticeboard
Where noticeno = #{noticeno}

mapper.update:
Update noticeboard
Set noticesubject = #{noticesubject} , noticecontents = #{noticecontents}
Where noticeno = #{noticeno}

